"use client";

import type { HttpError, BaseRecord } from "@refinedev/core";
import type { UseTableReturnType } from "@refinedev/react-table";
import type { Column } from "@tanstack/react-table";
import { flexRender } from "@tanstack/react-table";
import { Loader2 } from "lucide-react";
import { useEffect, useRef, useState } from "react";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/registry/new-york/ui/table";
import { DataTablePagination } from "@/registry/new-york/refine-ui/data-table/data-table-pagination";
import { cn } from "@/lib/utils";

type DataTableProps<TData extends BaseRecord> = {
  table: UseTableReturnType<TData, HttpError>;
};

export function DataTable<TData extends BaseRecord>({
  table,
}: DataTableProps<TData>) {
  const {
    reactTable: { getHeaderGroups, getRowModel, getAllColumns },
    refineCore: {
      tableQuery,
      currentPage,
      setCurrentPage,
      pageCount,
      pageSize,
      setPageSize,
    },
  } = table;

  const columns = getAllColumns();
  const leafColumns = table.reactTable.getAllLeafColumns();
  const isLoading = tableQuery.isLoading;

  const tableContainerRef = useRef<HTMLDivElement>(null);
  const tableRef = useRef<HTMLTableElement>(null);
  const [isOverflowing, setIsOverflowing] = useState({
    horizontal: false,
    vertical: false,
  });

  useEffect(() => {
    const checkOverflow = () => {
      if (tableRef.current && tableContainerRef.current) {
        const table = tableRef.current;
        const container = tableContainerRef.current;

        const horizontalOverflow = table.offsetWidth > container.clientWidth;
        const verticalOverflow = table.offsetHeight > container.clientHeight;

        setIsOverflowing({
          horizontal: horizontalOverflow,
          vertical: verticalOverflow,
        });
      }
    };

    checkOverflow();

    // Check on window resize
    window.addEventListener("resize", checkOverflow);

    // Check when table data changes
    const timeoutId = setTimeout(checkOverflow, 100);

    return () => {
      window.removeEventListener("resize", checkOverflow);
      clearTimeout(timeoutId);
    };
  }, [tableQuery.data?.data, pageSize]);

  return (
    <div className={cn("flex", "flex-col", "flex-1", "gap-4")}>
      <div ref={tableContainerRef} className={cn("rounded-md", "border")}>
        <Table ref={tableRef} style={{ tableLayout: "fixed", width: "100%" }}>
          <TableHeader>
            {getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  const isPlaceholder = header.isPlaceholder;

                  return (
                    <TableHead
                      key={header.id}
                      style={{
                        ...getCommonStyles({
                          column: header.column,
                          isOverflowing: isOverflowing,
                        }),
                      }}
                    >
                      {isPlaceholder ? null : (
                        <div className={cn("flex", "items-center", "gap-1")}>
                          {flexRender(
                            header.column.columnDef.header,
                            header.getContext(),
                          )}
                        </div>
                      )}
                    </TableHead>
                  );
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody className="relative">
            {isLoading ? (
              <>
                {Array.from({ length: pageSize < 1 ? 1 : pageSize }).map(
                  (_, rowIndex) => (
                    <TableRow
                      key={`skeleton-row-${rowIndex}`}
                      aria-hidden="true"
                    >
                      {leafColumns.map((column) => (
                        <TableCell
                          key={`skeleton-cell-${rowIndex}-${column.id}`}
                          style={{
                            ...getCommonStyles({
                              column,
                              isOverflowing: isOverflowing,
                            }),
                          }}
                          className={cn("truncate")}
                        >
                          <div className="h-8" />
                        </TableCell>
                      ))}
                    </TableRow>
                  ),
                )}
                <TableRow>
                  <TableCell
                    colSpan={columns.length}
                    className={cn("absolute", "inset-0", "pointer-events-none")}
                  >
                    <Loader2
                      className={cn(
                        "absolute",
                        "top-1/2",
                        "left-1/2",
                        "animate-spin",
                        "text-primary",
                        "h-8",
                        "w-8",
                        "-translate-x-1/2",
                        "-translate-y-1/2",
                      )}
                    />
                  </TableCell>
                </TableRow>
              </>
            ) : getRowModel().rows?.length ? (
              getRowModel().rows.map((row) => {
                return (
                  <TableRow
                    key={row.original?.id ?? row.id}
                    data-state={row.getIsSelected() && "selected"}
                  >
                    {row.getVisibleCells().map((cell) => {
                      return (
                        <TableCell
                          key={cell.id}
                          style={{
                            ...getCommonStyles({
                              column: cell.column,
                              isOverflowing: isOverflowing,
                            }),
                          }}
                        >
                          <div className="truncate">
                            {flexRender(
                              cell.column.columnDef.cell,
                              cell.getContext(),
                            )}
                          </div>
                        </TableCell>
                      );
                    })}
                  </TableRow>
                );
              })
            ) : (
              <DataTableNoData
                isOverflowing={isOverflowing}
                columnsLength={columns.length}
              />
            )}
          </TableBody>
        </Table>
      </div>
      {!isLoading && getRowModel().rows?.length > 0 && (
        <DataTablePagination
          currentPage={currentPage}
          pageCount={pageCount}
          setCurrentPage={setCurrentPage}
          pageSize={pageSize}
          setPageSize={setPageSize}
          total={tableQuery.data?.total}
        />
      )}
    </div>
  );
}

function DataTableNoData({
  isOverflowing,
  columnsLength,
}: {
  isOverflowing: { horizontal: boolean; vertical: boolean };
  columnsLength: number;
}) {
  return (
    <TableRow className="hover:bg-transparent">
      <TableCell
        colSpan={columnsLength}
        className={cn("relative", "text-center")}
        style={{ height: "490px" }}
      >
        <div
          className={cn(
            "absolute",
            "inset-0",
            "flex",
            "flex-col",
            "items-center",
            "justify-center",
            "gap-2",
            "bg-background",
          )}
          style={{
            position: isOverflowing.horizontal ? "sticky" : "absolute",
            left: isOverflowing.horizontal ? "50%" : "50%",
            transform: "translateX(-50%)",
            zIndex: isOverflowing.horizontal ? 2 : 1,
            width: isOverflowing.horizontal ? "fit-content" : "100%",
            minWidth: "300px",
          }}
        >
          <div className={cn("text-lg", "font-semibold", "text-foreground")}>
            No data to display
          </div>
          <div className={cn("text-sm", "text-muted-foreground")}>
            This table is empty for the time being.
          </div>
        </div>
      </TableCell>
    </TableRow>
  );
}

export function getCommonStyles<TData>({
  column,
  isOverflowing,
}: {
  column: Column<TData>;
  isOverflowing: {
    horizontal: boolean;
    vertical: boolean;
  };
}): React.CSSProperties {
  const isPinned = column.getIsPinned();
  const isLastLeftPinnedColumn =
    isPinned === "left" && column.getIsLastColumn("left");
  const isFirstRightPinnedColumn =
    isPinned === "right" && column.getIsFirstColumn("right");

  return {
    boxShadow:
      isOverflowing.horizontal && isLastLeftPinnedColumn
        ? "-4px 0 4px -4px var(--border) inset"
        : isOverflowing.horizontal && isFirstRightPinnedColumn
          ? "4px 0 4px -4px var(--border) inset"
          : undefined,
    left:
      isOverflowing.horizontal && isPinned === "left"
        ? `${column.getStart("left")}px`
        : undefined,
    right:
      isOverflowing.horizontal && isPinned === "right"
        ? `${column.getAfter("right")}px`
        : undefined,
    opacity: 1,
    position: isOverflowing.horizontal && isPinned ? "sticky" : "relative",
    background: isOverflowing.horizontal && isPinned ? "var(--background)" : "",
    borderTopRightRadius:
      isOverflowing.horizontal && isPinned === "right"
        ? "var(--radius)"
        : undefined,
    borderBottomRightRadius:
      isOverflowing.horizontal && isPinned === "right"
        ? "var(--radius)"
        : undefined,
    borderTopLeftRadius:
      isOverflowing.horizontal && isPinned === "left"
        ? "var(--radius)"
        : undefined,
    borderBottomLeftRadius:
      isOverflowing.horizontal && isPinned === "left"
        ? "var(--radius)"
        : undefined,
    width: column.getSize(),
    zIndex: isOverflowing.horizontal && isPinned ? 1 : 0,
  };
}

DataTable.displayName = "DataTable";

"use client";

import { useState, useEffect, useMemo } from "react";
import { useTranslate, type CrudOperators } from "@refinedev/core";
import type { Column, Table as ReactTable } from "@tanstack/react-table";
import type { DateRange } from "react-day-picker";
import { Check, ChevronsUpDown, ListFilter, X } from "lucide-react";

import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Badge } from "@/registry/new-york/ui/badge";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/registry/new-york/ui/popover";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/registry/new-york/ui/command";
import { Separator } from "@/registry/new-york/ui/separator";
import { Calendar } from "@/registry/new-york/ui/calendar";
import { cn } from "@/lib/utils";

export type DataTableFilterDropdownProps<TData> = {
  column: Column<TData>;
  contentClassName?: string;
  triggerClassName?: string;
  children: (args: {
    isOpen: boolean;
    setIsOpen: React.Dispatch<React.SetStateAction<boolean>>;
  }) => React.ReactNode;
};

export function DataTableFilterDropdown<TData>({
  column,
  triggerClassName,
  contentClassName,
  children,
}: DataTableFilterDropdownProps<TData>) {
  const [isOpen, setIsOpen] = useState(false);

  const isFiltered = column.getIsFiltered();

  return (
    <Popover open={isOpen} onOpenChange={setIsOpen}>
      <PopoverTrigger asChild>
        <Button
          onClick={() => setIsOpen(true)}
          variant="ghost"
          size="icon"
          className={cn(
            "data-[state=open]:bg-accent",
            "w-5 h-5",
            {
              "text-primary": isFiltered,
              "text-muted-foreground": !isFiltered,
            },
            triggerClassName,
          )}
        >
          <ListFilter className={cn("!h-3", "!w-3")} />
        </Button>
      </PopoverTrigger>
      <PopoverContent
        align="start"
        className={cn("w-full", "shadow-sm", contentClassName)}
      >
        {children({ isOpen, setIsOpen })}
      </PopoverContent>
    </Popover>
  );
}

type DataTableFilterDropdownActionsProps = {
  className?: string;
  isClearDisabled?: boolean;
  isApplyDisabled?: boolean;
  onClear: () => void;
  onApply: () => void;
};

export function DataTableFilterDropdownActions({
  className,
  isClearDisabled,
  isApplyDisabled,
  onClear,
  onApply,
}: DataTableFilterDropdownActionsProps) {
  const t = useTranslate();

  return (
    <div
      className={cn(
        "flex",
        "items-center",
        "justify-between",
        "w-full",
        "gap-2",
        className,
      )}
    >
      <Button
        size="sm"
        variant="ghost"
        disabled={isClearDisabled}
        className={cn(
          "rounded-sm",
          "text-xs",
          "font-semibold",
          "text-muted-foreground",
        )}
        onClick={() => {
          onClear();
        }}
      >
        <X className={cn("w-3.5", "h-3.5", "text-muted-foreground")} />
        {t("buttons.clear", "Clear")}
      </Button>

      <Button
        size="sm"
        disabled={isApplyDisabled}
        className={cn("rounded-sm", "text-xs", "font-semibold")}
        onClick={() => {
          onApply();
        }}
      >
        {t("buttons.apply", "Apply")}
      </Button>
    </div>
  );
}

export type DataTableFilterDropdownTextProps<TData> = {
  column: Column<TData>;
  table: ReactTable<TData>;
  defaultOperator?: CrudOperators;
  operators?: CrudOperators[];
  placeholder?: string;
};

export function DataTableFilterDropdownText<TData>({
  column,
  table,
  operators = [
    "eq",
    "ne",
    "contains",
    "ncontains",
    "containss",
    "ncontainss",
    "startswith",
    "nstartswith",
    "startswiths",
    "nstartswiths",
    "endswith",
    "nendswith",
    "endswiths",
    "nendswiths",
    "in",
    "nin",
    "ina",
    "nina",
  ],
  defaultOperator = "eq",
  placeholder,
}: DataTableFilterDropdownTextProps<TData>) {
  const t = useTranslate();

  return (
    <DataTableFilterInput
      column={column}
      table={table}
      operators={operators}
      defaultOperator={defaultOperator}
      renderInput={({ value, onChange }) => (
        <Input
          type="text"
          placeholder={
            placeholder ?? t("table.filter.text.placeholder", "Filter by...")
          }
          value={value}
          onChange={(event) => {
            onChange(event.target.value);
          }}
        />
      )}
    />
  );
}

export type DataTableFilterDropdownNumericProps<TData> = {
  column: Column<TData>;
  table: ReactTable<TData>;
  defaultOperator?: CrudOperators;
  operators?: CrudOperators[];
  placeholder?: string;
};

export function DataTableFilterDropdownNumeric<TData>({
  column,
  table,
  operators = ["eq", "ne", "gt", "lt", "gte", "lte"],
  defaultOperator = "eq",
  placeholder,
}: DataTableFilterDropdownNumericProps<TData>) {
  const t = useTranslate();

  return (
    <DataTableFilterInput
      column={column}
      table={table}
      operators={operators}
      defaultOperator={defaultOperator}
      renderInput={({ value, onChange }) => (
        <Input
          type="number"
          placeholder={
            placeholder ?? t("table.filter.numeric.placeholder", "Filter by...")
          }
          value={value}
          onChange={(event) => {
            onChange(event.target.value);
          }}
        />
      )}
    />
  );
}

export type DataTableFilterComboboxProps<TData> = {
  column: Column<TData>;
  table?: ReactTable<TData>;
  options: { label: string; value: string }[];
  defaultOperator?: CrudOperators;
  operators?: CrudOperators[];
  placeholder?: string;
  noResultsText?: string;
  multiple?: boolean;
};

export function DataTableFilterCombobox<TData>({
  column,
  table,
  options,
  defaultOperator = "eq",
  operators = ["eq", "ne", "in", "nin"],
  placeholder,
  noResultsText,
  multiple = false,
}: DataTableFilterComboboxProps<TData>) {
  const t = useTranslate();
  const [isOpen, setIsOpen] = useState(false);

  return (
    <DataTableFilterInput
      column={column}
      table={table}
      operators={operators}
      defaultOperator={defaultOperator}
      renderInput={({ value, onChange }) => {
        const currentValues = multiple
          ? Array.isArray(value)
            ? value
            : value && typeof value === "string"
              ? [value]
              : []
          : value && typeof value === "string"
            ? [value]
            : [];

        const handleSelect = (optionValue: string) => {
          if (multiple) {
            const newValues = currentValues.includes(optionValue)
              ? currentValues.filter((v) => v !== optionValue)
              : [...currentValues, optionValue];
            onChange(newValues);
          } else {
            onChange(optionValue);
            setIsOpen(false);
          }
        };

        const handleRemove = (optionValue: string) => {
          if (multiple) {
            const newValues = currentValues.filter((v) => v !== optionValue);
            onChange(newValues);
          }
        };

        const getDisplayText = () => {
          if (currentValues.length === 0) {
            return (
              placeholder ?? t("table.filter.combobox.placeholder", "Select...")
            );
          }

          if (multiple) {
            return `${currentValues.length} selected`;
          }

          const selectedOption = options.find(
            (option) => option.value === currentValues[0],
          );
          return selectedOption ? selectedOption.label : currentValues[0];
        };

        const getSelectedLabels = () => {
          return currentValues.map((val) => {
            const option = options.find((opt) => opt.value === val);
            return { label: option ? option.label : val, value: val };
          });
        };

        return (
          <Popover open={isOpen} onOpenChange={setIsOpen}>
            <PopoverTrigger asChild>
              <Button
                variant="outline"
                role="combobox"
                aria-expanded={isOpen}
                className={cn(
                  "w-full",
                  "min-w-48",
                  "max-w-80",
                  "justify-start",
                  "h-auto",
                  "min-h-9",
                )}
              >
                <div className={cn("flex", "gap-2", "w-full")}>
                  {multiple && currentValues.length > 0 ? (
                    <div className={cn("flex", "flex-wrap", "gap-1", "flex-1")}>
                      {getSelectedLabels()
                        .slice(0, 3)
                        .map(({ label, value: val }) => (
                          <Badge
                            key={val}
                            variant="outline"
                            className={cn(
                              "inline-flex",
                              "items-center",
                              "gap-0",
                              "h-4",
                              "pr-0.5",
                              "rounded-sm",
                            )}
                          >
                            <span className={cn("text-[10px]", "leading-4")}>
                              {label}
                            </span>
                            <span
                              className={cn(
                                "inline-flex",
                                "items-center",
                                "justify-center",
                                "p-0",
                                "w-4",
                                "h-full",
                                "text-muted-foreground",
                                "hover:text-destructive",
                                "rounded-sm",
                                "cursor-pointer",
                                "transition-colors",
                              )}
                              onClick={(e) => {
                                e.preventDefault();
                                e.stopPropagation();
                                handleRemove(val);
                              }}
                            >
                              <X className={cn("!h-2", "!w-2")} />
                            </span>
                          </Badge>
                        ))}
                      {currentValues.length > 3 && (
                        <span
                          className={cn(
                            "text-xs",
                            "text-muted-foreground",
                            "px-1",
                          )}
                        >
                          +{currentValues.length - 3} more
                        </span>
                      )}
                    </div>
                  ) : (
                    <span
                      className={cn(
                        "truncate",
                        "flex-1",
                        "text-start",
                        "text-xs",
                        currentValues.length === 0 && "text-muted-foreground",
                      )}
                    >
                      {getDisplayText()}
                    </span>
                  )}

                  <ChevronsUpDown
                    className={cn("h-4", "w-4", "shrink-0", "opacity-50")}
                  />
                </div>
              </Button>
            </PopoverTrigger>
            <PopoverContent className={cn("w-[200px]", "p-0")} align="start">
              <Command>
                <CommandInput
                  placeholder={t("table.filter.combobox.search", "Search...")}
                />
                <CommandList>
                  <CommandEmpty>
                    {noResultsText ??
                      t(
                        "table.filter.combobox.noResults",
                        "Results not found.",
                      )}
                  </CommandEmpty>
                  <CommandGroup>
                    {options.map((option) => (
                      <CommandItem
                        key={option.value}
                        value={option.value}
                        onSelect={() => handleSelect(option.value)}
                        keywords={option.label?.split(" ") ?? []}
                      >
                        {option.label}
                        <Check
                          className={cn(
                            "ml-auto",
                            "h-4",
                            "w-4",
                            currentValues.includes(option.value)
                              ? "opacity-100"
                              : "opacity-0",
                          )}
                        />
                      </CommandItem>
                    ))}
                  </CommandGroup>
                </CommandList>
              </Command>
            </PopoverContent>
          </Popover>
        );
      }}
    />
  );
}

export type DataTableFilterDropdownDateSinglePickerProps<TData> = {
  column: Column<TData>;
  defaultOperator?: CrudOperators;
  formatDate?: (date: Date | undefined) => string | undefined;
};

export function DataTableFilterDropdownDateSinglePicker<TData>({
  column,
  defaultOperator = "eq",
  formatDate,
}: DataTableFilterDropdownDateSinglePickerProps<TData>) {
  const columnFilterValue = column.getFilterValue() as string;

  const parseDate = (value: string | undefined): Date | undefined => {
    if (!value) return undefined;

    const date = new Date(value);

    if (Number.isNaN(date.getTime())) return undefined;
    return date;
  };

  const [filterValue, setFilterValue] = useState<Date | undefined>(() =>
    parseDate(columnFilterValue),
  );

  useEffect(() => {
    column.columnDef.meta = {
      ...column.columnDef.meta,
      filterOperator: defaultOperator,
    };
  }, [defaultOperator, column]);

  useEffect(() => {
    setFilterValue(parseDate(columnFilterValue));
  }, [columnFilterValue]);

  const hasDate = !!filterValue;

  const handleApply = () => {
    if (!filterValue) return;

    const value = formatDate?.(filterValue) ?? filterValue.toISOString();
    column.setFilterValue(value);
  };

  return (
    <DataTableFilterDropdown
      column={column}
      contentClassName={cn("w-fit", "p-0")}
    >
      {({ setIsOpen }) => {
        return (
          <div
            className={cn("flex", "flex-col", "items-center")}
            onKeyDown={(event) => {
              if (!hasDate) return;
              if (event.key === "Enter") {
                handleApply();
                setIsOpen(false);
              }
            }}
          >
            <Calendar
              mode="single"
              selected={filterValue}
              onSelect={(date) => {
                setFilterValue(date);
              }}
            />

            <div className={cn("w-full")}>
              <Separator />
            </div>

            <DataTableFilterDropdownActions
              className={cn("p-4")}
              isApplyDisabled={!hasDate}
              onClear={() => {
                column.setFilterValue(undefined);
                setFilterValue(undefined);
                setIsOpen(false);
              }}
              onApply={() => {
                handleApply();
                setIsOpen(false);
              }}
            />
          </div>
        );
      }}
    </DataTableFilterDropdown>
  );
}

export type DataTableFilterDropdownDateRangePickerProps<TData> = {
  column: Column<TData>;
  defaultOperator?: CrudOperators;
  formatDateRange?: (dateRange: DateRange | undefined) => string[] | undefined;
};

export function DataTableFilterDropdownDateRangePicker<TData>({
  column,
  defaultOperator = "between",
  formatDateRange,
}: DataTableFilterDropdownDateRangePickerProps<TData>) {
  const columnFilterValue = column.getFilterValue() as string[];

  const parseDateRange = (
    value: string[] | undefined,
  ): DateRange | undefined => {
    if (!value || !Array.isArray(value) || value.length !== 2) return undefined;

    const from = value[0] ? new Date(value[0]) : undefined;
    const to = value[1] ? new Date(value[1]) : undefined;

    if (
      !from ||
      !to ||
      Number.isNaN(from.getTime()) ||
      Number.isNaN(to.getTime())
    )
      return undefined;
    return { from, to };
  };

  const [filterValue, setFilterValue] = useState<DateRange | undefined>(() =>
    parseDateRange(columnFilterValue),
  );

  useEffect(() => {
    column.columnDef.meta = {
      ...column.columnDef.meta,
      filterOperator: defaultOperator,
    };
  }, [defaultOperator, column]);

  useEffect(() => {
    setFilterValue(parseDateRange(columnFilterValue));
    // eslint-disable-next-line react-hooks/exhaustive-deps -- objects are always different
  }, [JSON.stringify(columnFilterValue)]);

  const hasDateRange = filterValue?.from && filterValue?.to;

  const handleApply = () => {
    if (!filterValue?.from || !filterValue?.to) return;

    const values = formatDateRange?.(filterValue) ?? [
      filterValue.from.toISOString(),
      filterValue.to.toISOString(),
    ];
    column.setFilterValue(values);
  };

  return (
    <DataTableFilterDropdown
      column={column}
      contentClassName={cn("w-fit", "p-0")}
    >
      {({ setIsOpen }) => {
        return (
          <div
            className={cn("flex", "flex-col", "items-center")}
            onKeyDown={(event) => {
              if (!hasDateRange) return;
              if (event.key === "Enter") {
                handleApply();
                setIsOpen(false);
              }
            }}
          >
            <Calendar
              mode="range"
              numberOfMonths={2}
              selected={filterValue}
              onSelect={(date) => {
                setFilterValue({
                  from: date?.from,
                  to: date?.to,
                });
              }}
            />

            <div className={cn("w-full")}>
              <Separator />
            </div>

            <DataTableFilterDropdownActions
              className={cn("p-4")}
              isApplyDisabled={!hasDateRange}
              onClear={() => {
                column.setFilterValue(undefined);
                setFilterValue(undefined);
                setIsOpen(false);
              }}
              onApply={() => {
                handleApply();
                setIsOpen(false);
              }}
            />
          </div>
        );
      }}
    </DataTableFilterDropdown>
  );
}

export type DataTableFilterInputProps<TData> = {
  column: Column<TData>;
  table?: ReactTable<TData>;
  defaultOperator?: CrudOperators;
  operators?: CrudOperators[];
  renderInput: (props: {
    value: string | string[];
    onChange: (value: string | string[]) => void;
  }) => React.ReactNode;
};

export function DataTableFilterInput<TData>({
  column: columnFromProps,
  table: tableFromProps,
  operators: operatorsFromProps,
  defaultOperator: defaultOperatorFromProps,
  renderInput,
}: DataTableFilterInputProps<TData>) {
  const [filterValue, setFilterValue] = useState(
    (columnFromProps.getFilterValue() as string | string[]) || "",
  );

  const [operator, setOperator] = useState<CrudOperators>(() => {
    if (!tableFromProps) {
      return defaultOperatorFromProps || "eq";
    }

    const columnFilter = tableFromProps
      .getState()
      .columnFilters.find((filter) => {
        return filter.id === columnFromProps.id;
      });

    if (columnFilter && "operator" in columnFilter) {
      return columnFilter.operator as CrudOperators;
    }

    return defaultOperatorFromProps || "eq";
  });

  useEffect(() => {
    columnFromProps.columnDef.meta = {
      ...columnFromProps.columnDef.meta,
      filterOperator: operator,
    };
  }, [columnFromProps, operator]);

  const handleApply = () => {
    columnFromProps.setFilterValue(filterValue);
  };

  const handleClear = () => {
    columnFromProps.setFilterValue(undefined);
    setFilterValue("");
  };

  const handleOperatorChange = (value: CrudOperators) => {
    setOperator(value);
    columnFromProps.columnDef.meta = {
      ...columnFromProps.columnDef.meta,
      filterOperator: value,
    };
  };

  return (
    <DataTableFilterDropdown column={columnFromProps}>
      {({ setIsOpen }) => {
        return (
          <div
            className={cn(
              "flex",
              "flex-col",
              "items-center",
              "gap-4",
              "w-full",
            )}
            onKeyDown={(event) => {
              if (event.key === "Enter") {
                handleApply();
                setIsOpen(false);
              }
            }}
          >
            <div
              className={cn("grid", "grid-cols-1", "md:grid-cols-2", "gap-2")}
            >
              {operatorsFromProps && operatorsFromProps.length > 1 && (
                <DataTableFilterOperatorSelect
                  value={operator}
                  operators={operatorsFromProps}
                  onValueChange={handleOperatorChange}
                />
              )}
              {renderInput({
                value: filterValue,
                onChange: setFilterValue,
              })}
            </div>
            <div className={cn("w-full")}>
              <Separator />
            </div>
            <DataTableFilterDropdownActions
              onClear={() => {
                handleClear();
                setIsOpen(false);
              }}
              onApply={() => {
                handleApply();
                setIsOpen(false);
              }}
            />
          </div>
        );
      }}
    </DataTableFilterDropdown>
  );
}

